typedef long long ll;#define mod 1000000007ll add(ll x, ll y){    return (x+y)%mod;}ll mul(ll x, ll y){    return (x%mod)*(y%mod)%mod;}ll powll(ll x, ll y){    ll res = 1LL;    while(y){        if (y & 1LL)            res *= x;        res %= mod;        x = (x*x) % mod;        y >>= 1LL;    }    return res;}ll divll(ll x, ll y){    return (x * powll(y,mod-2)) % mod;}ll nPr(ll n, ll r){    ll res = 1LL;    FOR(i,1,r){        res = (res * ((n-i+1) % mod)) % mod;    }    return res;}ll nCr(ll n, ll r){    ll res = nPr(n,r);    FOR(i,1,r){        res = (res * powll(i,mod-2)) % mod;    }    return res;}